import React, {
  FunctionComponent,
  useState,
  ChangeEvent,
  KeyboardEvent
} from "react";
import {
  Paper,
  Grid,
  TextField,
  TextFieldProps,
  Button,
  ButtonProps
} from "@material-ui/core";

export type AddItemProps = TextFieldProps & {
  buttonProps?: ButtonProps;
};

export const AddItem: FunctionComponent<AddItemProps> = ({
  buttonProps,
  ...other
}) => {
  const textField = (
    <TextField placeholder="Add item here" fullWidth {...other} />
  );

  return (
    <Paper elevation={0} style={{ marginBottom: 16, padding: 16 }}>
      {buttonProps ? (
        <Grid container>
          <Grid xs={10} md={11} item style={{ paddingRight: 16 }}>
            {textField}
          </Grid>
          <Grid xs={2} md={1} item>
            <Button
              fullWidth
              color="secondary"
              variant="outlined"
              {...buttonProps}
            >
              Add
            </Button>
          </Grid>
        </Grid>
      ) : (
        textField
      )}
    </Paper>
  );
};

export const useAddItem = (add: (value: string) => void) => {
  const [value, setValue] = useState("");

  const changeInput = (event: ChangeEvent<HTMLInputElement>) =>
    setValue(event.target.value);

  const clearInputAndAdd = () => {
    setValue("");
    add(value);
  };

  const keyInput = (event: KeyboardEvent<HTMLInputElement>) => {
    if (event.key === "Enter") {
      clearInputAndAdd();
      return true;
    }

    return false;
  };

  return {
    value,
    changeInput,
    clearInputAndAdd,
    keyInput
  };
};

export interface StatefulAddItemProps {
  add: (value: string) => void;
  button?: boolean;
}

export const StatefulAddItem: FunctionComponent<StatefulAddItemProps> = ({
  add,
  button = true
}) => {
  const { value, changeInput, clearInputAndAdd, keyInput } = useAddItem(add);

  return (
    <AddItem
      value={value}
      onChange={changeInput}
      onKeyPress={keyInput}
      buttonProps={button ? { onClick: clearInputAndAdd } : undefined}
    />
  );
};

export default AddItem;

import React, { FunctionComponent } from "react";
import {
  CardHeader as MuiCardHeader,
  CardHeaderProps as MuiCardHeaderProps,
  TextFieldProps,
  TypographyVariant
} from "@material-ui/core";
import { StatefulInlineText as InlineText } from "./InlineText";

export interface CardHeaderProps {
  title: string;
  titleVariant?: TypographyVariant;
  titleLabel?: string;
  titleHelperText?: string;
  titleError?: boolean;
  titleEnter?: (title: string) => void;
  titleTextFieldProps?: TextFieldProps;
}

export type BaseCardHeaderProps = Omit<MuiCardHeaderProps, "title">;
export type FullCardHeaderProps = CardHeaderProps & BaseCardHeaderProps;

export const CardHeader: FunctionComponent<FullCardHeaderProps> = ({
  title,
  titleVariant = "h5",
  titleLabel,
  titleHelperText,
  titleError,
  titleEnter,
  titleTextFieldProps,
  ...other
}) => {
  return titleEnter ? (
    <MuiCardHeader
      disableTypography
      title={
        <InlineText
          text={title}
          textClassName="MuiCardHeader-title"
          textVariant={titleVariant}
          label={titleLabel}
          helperText={titleHelperText}
          error={titleError}
          enter={titleEnter}
          textFieldProps={titleTextFieldProps}
        />
      }
      {...other}
    />
  ) : (
    <MuiCardHeader title={title} {...other} />
  );
};

export default CardHeader;

import React, { FunctionComponent, useState } from "react";
import { Typography, TypographyVariant } from "@material-ui/core";
import { StatefulTextField as TextField, TextFieldProps } from "./TextField";
import { Alert, AlertTitle } from "@material-ui/lab";
import { ErrorTooltip } from "./Tooltip";

export interface InlineTextProps {
  text: string;
  textClassName: string;
  textVariant: TypographyVariant;
  edited?: boolean;
  label?: string;
  helperText?: string;
  error?: boolean;
  edit: () => void;
  enter: (value: string) => void;
  textFieldProps?: TextFieldProps;
}

export const InlineText: FunctionComponent<InlineTextProps> = ({
  text,
  textClassName,
  textVariant,
  edited,
  label,
  helperText,
  error,
  edit,
  enter,
  textFieldProps
}) => {
  if (edited) {
    return (
      <TextField
        defaultValue={text}
        label={label}
        helperText={helperText}
        error={error}
        enter={enter}
        {...textFieldProps}
      />
    );
  }

  if (error) {
    const alert = (
      <Alert variant="outlined" severity="error" onClick={edit}>
        <AlertTitle>{text}</AlertTitle>
      </Alert>
    );

    return helperText ? (
      <ErrorTooltip title={helperText}>{alert}</ErrorTooltip>
    ) : (
      alert
    );
  }

  return (
    <Typography
      variant={textVariant}
      className={textClassName}
      component="span"
      display="block"
      onClick={edit}
    >
      {text}
    </Typography>
  );
};

export const StatefulInlineText: FunctionComponent<Omit<
  InlineTextProps,
  "edited" | "edit"
>> = ({
  text,
  textClassName,
  textVariant,
  label,
  helperText,
  error,
  enter,
  textFieldProps
}) => {
  const [edited, setEdited] = useState(false);

  return (
    <InlineText
      text={text}
      textClassName={textClassName}
      textVariant={textVariant}
      edited={edited}
      label={label}
      helperText={helperText}
      error={error}
      edit={() => setEdited(true)}
      enter={(value: string) => {
        setEdited(false);
        enter(value);
      }}
      textFieldProps={textFieldProps}
    />
  );
};

export default InlineText;

import React, { FunctionComponent } from "react";
import { CheckboxProps, IconButtonProps } from "@material-ui/core";
import { List as MuiList, ListProps as MuiListProps } from "../mui-base";
import { BaseListItemTextProps } from "./ListItemText";
import { ListItem, ListItemProps } from "./ListItem";
import {
  StatefulAddItem as AddItem,
  StatefulAddItemProps as AddItemProps
} from "./AddItem";

export interface ListProps {
  droppableId?: string;
  addItemProps?: AddItemProps;
  listItems?: ListItemProps[];
  checkboxProps?: Omit<CheckboxProps, "checked">;
  listItemTextProps?: BaseListItemTextProps;
  deleteButtonProps?: IconButtonProps;
}

export type BaseListProps = MuiListProps;
export type FullListProps = ListProps & BaseListProps;

export const List: FunctionComponent<FullListProps> = ({
  droppableId,
  addItemProps,
  listItems,
  checkboxProps,
  listItemTextProps,
  deleteButtonProps,
  ...other
}) => {
  const dnd = Boolean(droppableId);
  const droppableProps = (dnd && { droppableId }) as
    | false
    | { droppableId: string };

  return (
    <>
      {addItemProps && <AddItem {...addItemProps} />}
      <MuiList droppableProps={droppableProps} {...other}>
        {listItems &&
          listItems.map((listItem, index) => {
            const id = listItem.itemId;

            return (
              <ListItem
                draggableProps={dnd && { draggableId: id, index }}
                key={id}
                checkboxProps={checkboxProps}
                listItemTextProps={listItemTextProps}
                deleteButtonProps={deleteButtonProps}
                {...listItem}
              />
            );
          })}
      </MuiList>
    </>
  );
};

export default List;

import React, { FunctionComponent } from "react";
import { Card, CardContent, CardProps } from "@material-ui/core";
import { CardHeader, CardHeaderProps, BaseCardHeaderProps } from "./CardHeader";
import { List, ListProps, BaseListProps } from "./List";

export interface ListCardProps extends CardHeaderProps, ListProps {
  cardHeaderProps?: BaseCardHeaderProps;
  listProps?: BaseListProps;
}

export type BaseListCardProps = CardProps;
export type FullListCardProps = ListCardProps & CardProps;

export const ListCard: FunctionComponent<FullListCardProps> = ({
  droppableId,

  title,
  titleVariant,
  titleLabel,
  titleHelperText,
  titleError,
  titleEnter,
  titleTextFieldProps,
  cardHeaderProps,

  addItemProps,
  listItems,
  checkboxProps,
  listItemTextProps,
  deleteButtonProps,
  listProps,

  ...other
}) => {
  return (
    <Card {...other}>
      <CardHeader
        title={title}
        titleLabel={titleLabel}
        titleHelperText={titleHelperText}
        titleError={titleError}
        titleEnter={titleEnter}
        titleTextFieldProps={titleTextFieldProps}
        {...cardHeaderProps}
      />
      <CardContent>
        <List
          droppableId={droppableId}
          addItemProps={addItemProps}
          listItems={listItems}
          checkboxProps={checkboxProps}
          listItemTextProps={listItemTextProps}
          deleteButtonProps={deleteButtonProps}
          {...listProps}
        />
      </CardContent>
    </Card>
  );
