import React, { FunctionComponent } from "react";
import {
  Droppable,
  DroppableProps,
  DroppableProvided,
  DroppableStateSnapshot
} from "react-beautiful-dnd";
import clsx from "clsx";
import { BaseList, BaseListPropsWithoutRef } from "./BaseList";

export type DroppablePropsWithoutChildren = Omit<DroppableProps, "children">;

export interface DroppableListProps extends BaseListPropsWithoutRef {
  droppableProps: DroppablePropsWithoutChildren;
}

export const DroppableList: FunctionComponent<DroppableListProps> = ({
  children,
  className,
  droppableProps,
  ...other
}) => (
  <Droppable {...droppableProps}>
    {(
      { innerRef, droppableProps, placeholder }: DroppableProvided,
      { isDraggingOver }: DroppableStateSnapshot
    ) => (
      <BaseList
        className={clsx(className, { isDraggingOver })}
        ref={innerRef}
        {...droppableProps}
        {...other}
      >
        {children}
        {placeholder}
      </BaseList>
    )}
  </Droppable>
);

import React, { FunctionComponent } from "react";
import { DroppableList, DroppablePropsWithoutChildren } from "./DroppableList";
import { BaseList, BaseListPropsWithoutRef } from "./BaseList";

export interface ListProps extends BaseListPropsWithoutRef {
  droppableProps?: DroppablePropsWithoutChildren | false;
}

export const List: FunctionComponent<ListProps> = ({
  children,
  droppableProps,
  ...other
}) => {
  if (droppableProps) {
    return (
      <DroppableList droppableProps={droppableProps} {...other}>
        {children}
      </DroppableList>
    );
  }

  return <BaseList {...other}>{children}</BaseList>;
};

import React, { FunctionComponent } from "react";
import {
  Draggable,
  DraggableProps,
  DraggableProvided,
  DraggableStateSnapshot
} from "react-beautiful-dnd";
import clsx from "clsx";
import { BaseListItem, BaseListItemPropsWithoutRef } from "./BaseListItem";

export type DraggablePropsWithoutChildren = Omit<DraggableProps, "children">;

export interface DraggableListItemProps extends BaseListItemPropsWithoutRef {
  draggableProps: DraggablePropsWithoutChildren;
}

export const DraggableListItem: FunctionComponent<DraggableListItemProps> = ({
  children,
  className,
  draggableProps,
  ...other
}) => (
  <Draggable {...draggableProps}>
    {(
      { innerRef, draggableProps, dragHandleProps }: DraggableProvided,
      { isDragging }: DraggableStateSnapshot
    ) => (
      <BaseListItem
        className={clsx(className, { isDragging })}
        ref={innerRef}
        {...draggableProps}
        {...dragHandleProps}
        role="listitem"
        draggable
        {...other}
      >
        {children}
      </BaseListItem>
    )}
  </Draggable>
);

import React, { FunctionComponent } from "react";
import {
  DraggableListItem,
  DraggablePropsWithoutChildren
} from "./DraggableListItem";
import { BaseListItem, BaseListItemPropsWithoutRef } from "./BaseListItem";

export interface ListItemProps extends BaseListItemPropsWithoutRef {
  draggableProps?: DraggablePropsWithoutChildren | false;
}

export const ListItem: FunctionComponent<ListItemProps> = ({
  children,
  draggableProps,
  ...other
}) => {
  if (draggableProps) {
    return (
      <DraggableListItem draggableProps={draggableProps} {...other}>
        {children}
      </DraggableListItem>
    );
  }

  return <BaseListItem {...other}>{children}</BaseListItem>;
};

