import React from "react";
import { createStore, applyMiddleware } from "redux";
import { Provider } from "react-redux";
import createSagaMiddleware from "redux-saga";
import { createLogger } from "redux-logger";
import { combinedReducer } from "./reducers";
import { mainSaga, enableLocalStorageSaga } from "./sagas";
import { Layout } from "./Layout";

const localStorageId = "todos";

const logger = createLogger({ collapsed: true });
const sagaMiddleware = createSagaMiddleware();

export const store = createStore(
  combinedReducer,
  applyMiddleware(logger, sagaMiddleware)
);

sagaMiddleware.run(mainSaga);
sagaMiddleware.run(enableLocalStorageSaga, localStorageId);

function App() {
  return (
    <Provider store={store}>
      <Layout />
    </Provider>
  );
}

export default App;

import React from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  Card,
  CardHeader,
  CardContent,
  Typography,
  Button
} from "@material-ui/core";
import { TodoState, TodoStates, TodosState } from "./types";
import { toggleTodo } from "./action-creators";
import { useStyles } from "./TodoList";
import { rootId } from "./todo";

export function CurrentTodo({ viewId }: { viewId: string }) {
  const classes = useStyles();
  const dispatch = useDispatch();

  const { views, parts } = useSelector(
    (state: { todos: TodosState }) => state.todos
  );

  const noTodos = !parts[rootId]?.length;

  let partId = views[viewId].partId;
  let todos: TodoStates;
  let todo: TodoState | undefined;

  do {
    todos = parts[partId];

    if (!todos) {
      break;
    }

    let newTodo = todos.find((todo) => !todo.checked);

    if (!newTodo) {
      break;
    }

    todo = newTodo;
    partId = todo.id;
  } while (1);

  const id = todo?.id;

  return (
    <Card classes={{ root: classes.card }}>
      <CardHeader
        action={
          id ? (
            <Button
              variant={"contained"}
              disableRipple
              color="secondary"
              onClick={() => dispatch(toggleTodo({ viewId, id }))}
            >
              Done
            </Button>
          ) : undefined
        }
      />
      <CardContent>
        {noTodos ? (
          <Typography variant="h4" align="center">
            {"Please enter something"}
          </Typography>
        ) : (
          <Typography variant="h2" align="center">
            {todo ? todo.title : "ðŸ¥³ ALL DONE"}
          </Typography>
        )}
      </CardContent>
    </Card>
  );
}

import React from "react";
import {
  Card,
  CardContent,
  CardActions,
  Collapse,
  IconButton,
  Grid
} from "@material-ui/core";
import { Close, ExpandMore } from "@material-ui/icons";
import { makeStyles, Theme, createStyles } from "@material-ui/core/styles";
import { useSelector, useDispatch } from "react-redux";
import clsx from "clsx";
import { TodoList } from "./TodoList";
import { CurrentTodo } from "./CurrentTodo";
import { UI, closeSubView } from "./ui";
import "./ListItem.css";
import "./IconButton.css";

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    card: {
      borderRadius: 0,
      backgroundColor: theme.palette.primary.main,
      color: theme.palette.primary.contrastText,
      boxShadow: "none"
    },
    expand: {
      transform: "rotate(0deg)",
      marginLeft: "auto",
      color: theme.palette.primary.contrastText,
      transition: theme.transitions.create("transform", {
        duration: theme.transitions.duration.shortest
      })
    },
    expandOpen: {
      transform: "rotate(180deg)"
    }
  })
);

export const defaultTitle = "TODOS";

export const Layout = () => {
  const classes = useStyles();
  const dispatch = useDispatch();
  const [expanded, setExpanded] = React.useState(true);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  const { mainViewId: viewId, subViewId }: UI = useSelector(
    (state: { ui: UI }) => state.ui
  );

  return (
    <Card classes={{ root: classes.card }}>
      <CardContent>
        <CurrentTodo viewId={viewId} />
      </CardContent>
      <CardActions disableSpacing>
        <IconButton
          className={clsx(classes.expand, {
            [classes.expandOpen]: expanded
          })}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          <ExpandMore />
        </IconButton>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <Grid container>
          <Grid item xs={12} md={subViewId ? 6 : 12}>
            <CardContent>
              <TodoList viewId={viewId} title={defaultTitle} />
            </CardContent>
          </Grid>
          {subViewId && (
            <Grid item xs={12} md={6}>
              <CardContent>
                <TodoList
                  viewId={subViewId}
                  cardHeaderProps={{
                    action: (
                      <IconButton
                        aria-label="Close item"
                        onClick={() => dispatch(closeSubView({ subViewId }))}
                      >
                        <Close />
                      </IconButton>
                    )
                  }}
                />
              </CardContent>
            </Grid>
          )}
        </Grid>
      </Collapse>
    </Card>
  );
};

import React, { FunctionComponent, useState, MouseEvent } from "react";
import { useDispatch, useSelector } from "react-redux";
import { IconButton, Popover, MenuList, MenuItem } from "@material-ui/core";
import { MoreVert } from "@material-ui/icons";
import { VisibilityFilter, TodosState } from "./types";
import { setVisibilityFilter } from "./action-creators";

export const Menu: FunctionComponent<{ viewId: string }> = ({ viewId }) => {
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const dispatch = useDispatch();

  const visibilityFilter = useSelector(
    (state: { todos: TodosState }) => state.todos.views[viewId].visibilityFilter
  );

  const open = Boolean(anchorEl);

  const handleClick = (event: MouseEvent<HTMLButtonElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const changeFilter = (visibilityFilter: VisibilityFilter) => () => {
    dispatch(setVisibilityFilter({ viewId, visibilityFilter }));
    handleClose();
  };

  return (
    <div>
      <IconButton
        aria-label="setting"
        aria-controls="menu"
        aria-haspopup="true"
        onClick={handleClick}
      >
        <MoreVert />
      </IconButton>
      <Popover
        id="menu"
        open={open}
        anchorEl={anchorEl}
        onClose={handleClose}
        anchorOrigin={{
          vertical: "bottom",
          horizontal: "center"
        }}
        transformOrigin={{
          vertical: "top",
          horizontal: "center"
        }}
      >
        <MenuList dense>
          {visibilityFilter !== VisibilityFilter.SHOW_ALL && (
            <MenuItem onClick={changeFilter(VisibilityFilter.SHOW_ALL)}>
              Show all
            </MenuItem>
          )}
          {visibilityFilter !== VisibilityFilter.SHOW_ACTIVE && (
            <MenuItem onClick={changeFilter(VisibilityFilter.SHOW_ACTIVE)}>
              Show active
            </MenuItem>
          )}
          {visibilityFilter !== VisibilityFilter.SHOW_COMPLETED && (
            <MenuItem onClick={changeFilter(VisibilityFilter.SHOW_COMPLETED)}>
              Show completed
            </MenuItem>
          )}
          {visibilityFilter !== VisibilityFilter.SHOW_INVALID && (
            <MenuItem onClick={changeFilter(VisibilityFilter.SHOW_INVALID)}>
              Show invalid
            </MenuItem>
          )}
        </MenuList>
      </Popover>
    </div>
  );
};

export default Menu;

import React from "react";
import { makeStyles, Theme } from "@material-ui/core/styles";
import { useSelector, useDispatch } from "react-redux";
import { DragDropContext, DropResult } from "react-beautiful-dnd";
import { TodosState } from "./types";
import {
  addTodo,
  deleteTodo,
  expandTodo,
  updateTodoTitle,
  toggleTodo,
  moveTodo
} from "./action-creators";
import { ListCard as List, BaseCardHeaderProps } from "../../core";
import Menu from "./Menu";

export const useStyles = makeStyles((theme: Theme) => ({
  card: {
    borderRadius: 0,
    backgroundColor: theme.palette.primary.light,
    color: theme.palette.primary.contrastText,
    boxShadow: "none"
  }
}));

export function TodoList({
  viewId,
  title,
  cardHeaderProps
}: {
  viewId: string;
  title?: string;
  cardHeaderProps?: BaseCardHeaderProps;
}) {
  const classes = useStyles();
  const dispatch = useDispatch();

  const { parts, views, todos: allTodos } = useSelector(
    (state: { todos: TodosState }) => state.todos
  );

  const { todos, partId } = views[viewId];

  if (!title) {
    title = allTodos[partId].title;
  }

  return (
    <DragDropContext
      onDragEnd={(dropResult: DropResult) =>
        dispatch(moveTodo({ viewId, dropResult }))
      }
    >
      <List
        droppableId="drop-area"
        classes={{ root: classes.card }}
        title={title}
        addItemProps={{
          add: (title: string) => dispatch(addTodo({ viewId, title }))
        }}
        listItems={todos.map((todo) => {
          const errors = todo.errors;
          const id = todo.id;
          const expandButtonClassName = parts[id]?.length
            ? "hasElements"
            : undefined;

          return {
            itemId: id,
            primary: todo.title,
            primaryError: Boolean(errors),
            primaryHelperText: errors && errors.join(", "),
            primaryEnter: (title: string) =>
              dispatch(updateTodoTitle({ viewId, id, title })),
            checked: todo.checked,
            checkboxProps: {
              onClick: () => dispatch(toggleTodo({ viewId, id }))
            },
            deleteButtonProps: {
              onClick: () => dispatch(deleteTodo({ viewId, id }))
            },
            expandButtonProps: {
              className: expandButtonClassName,
              onClick: () => dispatch(expandTodo({ viewId, id }))
            }
          };
        })}
        cardHeaderProps={
          cardHeaderProps || {
            action: <Menu viewId={viewId} />
          }
        }
      />
    </DragDropContext>
  );
}

import React from "react";
import { DragDropContext, DropResult } from "react-beautiful-dnd";
