import { StrictEffect } from "redux-saga/effects";

export type SagaGenerator<RT = any> = Generator<
  StrictEffect<any, any>,
  RT,
  any
>;

import { combineReducers, Action } from "redux";
import { put } from "redux-saga/effects";
import { SagaGenerator } from "../../generics";
import {
  MutableCombinedState,
  CombinedState,
  MutableReducerMap,
  CombinedReducer,
  Manager,
  MutableManagerMap,
  ManagerMap
} from "./types";
import { sagaMiddleware } from "./saga-manager";

export const makeCombinedManager = (
  initialManagers: readonly Manager<any>[]
) => {
  const managers: MutableManagerMap<any> = {};
  const reducers: MutableReducerMap<any> = {};

  const addToMaps = (manager: Manager<any>) => {
    const managerId = manager.managerId;

    if (reducers[managerId]) {
      throw new Error(
        `A manager was already combined with the name: ${managerId}`
      );
    }

    managers[managerId] = manager;
    reducers[managerId] = manager.reducer;

    manager.progenyHandler.getChildren().forEach(addToMaps);
  };

  const removeFromMaps = (managerId: Manager<any> | string) => {
    const manager =
      typeof managerId === "string" ? managers[managerId] : managerId;
    const id = manager.managerId;

    if (!manager) {
      return;
    }

    manager.progenyHandler.getChildren().forEach(removeFromMaps);

    delete managers[id];
    delete reducers[id];

    managerIdsToRemove.push(id);
  };

  initialManagers.forEach(addToMaps);

  // Type cannot be statically inferred by Typescript
  let combinedReducer = (combineReducers(
    reducers
  ) as unknown) as CombinedReducer;

  let managerIdsToRemove: string[] = [];

  const forEach = (
    fn: (
      manager: Omit<Manager<any>, "managerId">,
      managerId: string,
      managers: ManagerMap<any>
    ) => void
  ) => {
    Object.keys(managers).forEach((managerId) => {
      fn(managers[managerId], managerId, managers);
    });
  };

  const map = <U>(
    fn: (
      manager: Omit<Manager<any>, "managerId">,
      managerId: string,
      managers: ManagerMap<any>
    ) => U
  ) => {
    const map: { [key: string]: U } = {};

    Object.keys(managers).forEach((managerId) => {
      map[managerId] = fn(managers[managerId], managerId, managers);
    });

    return map;
  };

  const mapToArray = <U>(
    fn: (
      manager: Omit<Manager<any>, "managerId">,
      managerId: string,
      managers: ManagerMap<any>
    ) => U
  ) => {
    const map: U[] = [];

    Object.keys(managers).forEach((managerId) => {
      map.push(fn(managers[managerId], managerId, managers));
    });

    return map;
  };

  return {
    getManager: (managerId: string): Manager<any> => managers[managerId],

    getManagers: (): Manager<any>[] => Object.values(managers),

    getManagerIds: (): string[] => Object.keys(managers),

    forEach,
    map,
    mapToArray,

    add: (manager: Manager<any>) => {
      addToMaps(manager);

      // Type cannot be statically inferred by Typescript
      combinedReducer = (combineReducers(
        reducers
      ) as unknown) as CombinedReducer;
    },

    remove: (manager: Manager<any> | string) => {
      removeFromMaps(manager);

      // Type cannot be statically inferred by Typescript
      combinedReducer = (combineReducers(
        reducers
      ) as unknown) as CombinedReducer;
    },

    reducer: (state?: CombinedState, action?: Action) => {
      if (managerIdsToRemove.length > 0) {
        const newState: MutableCombinedState = { ...state };

        for (let managerId of managerIdsToRemove) {
          delete newState[managerId];
        }

        managerIdsToRemove = [];

        return combinedReducer(newState, action);
      }

      return combinedReducer(state, action);
    },

    sagaMiddleware,

    runSagas: () => {
      forEach(({ sagaManager }) => sagaManager.startAll());
      forEach(({ actionCreators: { ready } }) =>
        sagaMiddleware.run(function* (): SagaGenerator {
          yield put(ready());
        })
      );
    }
  };
};

import { all, put, take } from "redux-saga/effects";
import { SagaGenerator } from "../../generics";
import { PersistedItemMap, SelectionMap, Manager } from "./types";

export const enableLocalStorage = <T>(manager: Manager<T>) => {
  const {
    managerId: localStorageId,
    CONSTS: { READY },
    actionCreators: { set },
    progenyHandler: { getDescendants }
  } = manager;
  const ALL_READY = [READY].concat(
    getDescendants().map(({ CONSTS: { READY } }) => READY)
  );

  manager.sagaManager.add("loadFromLocalStorage", function* (): SagaGenerator {
    yield all(ALL_READY.map((READY) => take(READY)));

    const { todos: items, parts: selections } = JSON.parse(
      localStorage.getItem(localStorageId) || `{"todos":{},"parts":{}}`
    ) as { todos: PersistedItemMap<T>; parts: SelectionMap };

    yield put(set({ items, selections }));
  });
};

import { DropResult } from "react-beautiful-dnd";
import {
  Payload,
  PersistedItem,
  PayloadMap,
  PersistedItemMap,
  SelectionMap,
  ManagerConsts,
  ManagerAction,
  ActionCreatorMap,
  VisibilityFilter
} from "./types";

export const makeManagerActionCreators = <T>(
  CONSTS: ManagerConsts
): ActionCreatorMap<T> => {
  const {
    CREATE,
    DESTROY,
    MODIFY,
    SET,
    CLEAR,
    MOVE,

    DO_CREATE,
    DO_DESTROY,
    DO_MODIFY,
    DO_SET,
    DO_CLEAR,
    DO_MOVE,

    SET_VISIBILITY_FILTER,
    DO_SET_VISIBILITY_FILTER,

    EXPAND,
    DO_EXPAND,

    READY
  } = CONSTS;

  const create = (payload: PersistedItem<T>): ManagerAction<T> => ({
    type: CREATE,
    payload
  });

  const destroy = (itemId: string): ManagerAction<T> => ({
    type: DESTROY,
    itemId
  });

  const modify = (
    itemId: string,
    payload: PersistedItem<T>
  ): ManagerAction<T> => ({
    type: MODIFY,
    itemId,
    payload
  });

  const set = (payload: {
    items: PersistedItemMap<T>;
    selections: SelectionMap;
  }): ManagerAction<T> => ({
    type: SET,
    payload
  });

  const clear = (): ManagerAction<T> => ({
    type: CLEAR
  });

  const move = (itemId: string, payload: DropResult): ManagerAction<T> => {
    return {
      type: MOVE,
      itemId,
      payload
    };
  };

  const doCreate = (itemId: string, payload: Payload<T>): ManagerAction<T> => ({
    type: DO_CREATE,
    itemId,
    payload
  });

  const doDestroy = (itemId: string): ManagerAction<T> => ({
    type: DO_DESTROY,
    itemId
  });

  const doModify = (itemId: string, payload: Payload<T>): ManagerAction<T> => ({
    type: DO_MODIFY,
    itemId,
    payload
  });

  const doSet = (payload: {
    items: PayloadMap<T>;
    selections: SelectionMap;
  }): ManagerAction<T> => ({
    type: DO_SET,
    payload
  });

  const doClear = (): ManagerAction<T> => ({
    type: DO_CLEAR
  });

  const doMove = (itemId: string, payload: DropResult): ManagerAction<T> => {
    return {
      type: DO_MOVE,
      itemId,
      payload
    };
  };

  const setVisibilityFilter = (
    visibilityFilter: VisibilityFilter
  ): ManagerAction<T> => ({
    type: SET_VISIBILITY_FILTER,
    visibilityFilter
  });

  const doSetVisibilityFilter = (
    visibilityFilter: VisibilityFilter
  ): ManagerAction<T> => ({
    type: DO_SET_VISIBILITY_FILTER,
    visibilityFilter
  });

  const expand = (itemId: string, expanded: boolean): ManagerAction<T> => ({
    type: EXPAND,
    itemId,
    expanded
  });

  const doExpand = (itemId: string, expanded: boolean): ManagerAction<T> => ({
    type: DO_EXPAND,
    itemId,
    expanded
  });

  const ready = (): ManagerAction<T> => ({
    type: READY
  });

  return {
    create,
    destroy,
    modify,
    set,
    clear,
    move,

    doCreate,
    doDestroy,
    doModify,
    doSet,
    doClear,
    doMove,

    setVisibilityFilter,
    doSetVisibilityFilter,

    expand,
    doExpand,

    ready
  };
};

import { ManagerConsts } from "./types";

const makeConstants = (managerId: string, prefix: "" | "DO_" = "") => {
  return {
    [prefix + "CREATE"]: managerId + "_" + prefix + "CREATE",
    [prefix + "DESTROY"]: managerId + "_" + prefix + "DESTROY",
    [prefix + "MODIFY"]: managerId + "_" + prefix + "MODIFY",
    [prefix + "SET"]: managerId + "_" + prefix + "SET",
    [prefix + "CLEAR"]: managerId + "_" + prefix + "CLEAR",
    [prefix + "MOVE"]: managerId + "_" + prefix + "MOVE",
    [prefix + "SET_VISIBILITY_FILTER"]:
      managerId + "_" + prefix + "SET_VISIBILITY_FILTER",
    [prefix + "EXPAND"]: managerId + "_" + prefix + "EXPAND"
  };
};

export const makeManagerConstants = (managerId: string) => {
  return {
    ...makeConstants(managerId),
    ...makeConstants(managerId, "DO_"),
    READY: managerId + "_READY"
  } as ManagerConsts; // Trick Typescript to discriminate between constructed strings
};

import {
  Payload,
  Manager,
  ManagerProgenyHandler,
  ManagerRelationship
} from "./types";
import {
  addCreateSagas,
  addDestroySagas,
  addModifySagas,
  addSetSagas,
  addSetVisibilityFilterSagas,
  addExpandSagas
} from "./sagas";

export const makeManagerProgenyHandler = <T>(
  manager: Manager<T>,
  makeManager: <U>(managerId: string, parentManagerId?: string) => Manager<U>
): ManagerProgenyHandler<T> => {
  const children: Map<string, Manager<any>> = new Map();
  const managerId = manager.managerId;

  const addChild = <U>(
    childManagerId: string,
    {
      adaptToParent,
      adaptToChild,
      relationship,
      selectionId
    }: {
      adaptToParent?: (payload: Payload<U>) => Payload<T>;
      adaptToChild?: (payload: Payload<T>) => Payload<U>;
      relationship: ManagerRelationship;
      selectionId?: string;
    }
  ): Manager<U> => {
    if (children.has(childManagerId)) {
      return children.get(childManagerId)!;
    }

    const childManager = makeManager<U>(childManagerId, managerId);

    const sagaArgs = {
      manager,
      childManager,
      adaptToChild,
      adaptToParent,
      relationship,
      selectionId
    };

    addCreateSagas(sagaArgs);
    addDestroySagas(sagaArgs);
    addModifySagas(sagaArgs);
    addSetSagas(sagaArgs);
    addSetVisibilityFilterSagas(sagaArgs);
    addExpandSagas(sagaArgs);

    children.set(childManagerId, childManager);

    return childManager;
  };

  const hasChild = (managerId: string) => children.has(managerId);
  const hasDescendant = (managerId: string) => {
    if (children.has(managerId)) {
      return true;
    }

    for (let manager of children.values()) {
      if (manager.progenyHandler.hasDescendant(managerId)) {
        return true;
      }
    }

    return false;
  };

  const hasChildren = () => Boolean(children.size);
  const hasDescendants = () => {
    if (Boolean(children.size)) {
      return true;
    }

    for (let manager of children.values()) {
      if (manager.progenyHandler.hasDescendants()) {
        return true;
      }
    }

    return false;
  };

  const getChild = (managerId: string) => children.get(managerId);
  const getDescendant = (managerId: string) => {
    if (children.has(managerId)) {
      return children.get(managerId);
    }

    for (let manager of children.values()) {
      let descendant = manager.progenyHandler.getDescendant(managerId);
      if (descendant) {
        return descendant;
      }
    }
  };

  const getChildren = () => Array.from(children.values());
  const getDescendants = () => {
    let descendants: Manager<any>[] = [];

    for (let manager of children.values()) {
      descendants.push(manager);

      if (manager.progenyHandler.hasChildren()) {
        descendants = descendants.concat(
          manager.progenyHandler.getDescendants()
        );
      }
    }

    return descendants;
  };

  return {
    addChild,
    hasChild,
    hasDescendant,
    hasChildren,
    hasDescendants,
    getChild,
    getDescendant,
    getChildren,
    getDescendants
  };
};

import {
  ManagerState,
  ManagerConsts,
  ManagerReducer,
  ManagerAnswerAction,
  VisibilityFilter
} from "./types";

export const makeManagerReducer = <T>(
  CONSTS: ManagerConsts
): ManagerReducer<T> => {
  const initialState: ManagerState<T> = {
    items: new Map(),
    selections: new Map(),
    visibilityFilter: VisibilityFilter.SHOW_ACTIVE
  };

  const {
    DO_CREATE,
    DO_DESTROY,
    DO_MODIFY,
    DO_SET,
    DO_CLEAR,
    DO_SET_VISIBILITY_FILTER,
    DO_EXPAND
  } = CONSTS;

  const reducer = (
    state = initialState,
    action?: ManagerAnswerAction<T>
  ): ManagerState<T> => {
    if (action) {
      switch (action.type) {
        case DO_CREATE: {
          const { itemId } = action;

          if (!state.items.has(itemId)) {
            const newItems = new Map(state.items);
            newItems.set(itemId, action.payload);
            return { ...state, items: newItems };
          }

          return state;
        }

        case DO_DESTROY: {
          const newItems = new Map(state.items);
          newItems.delete(action.itemId);
          return { ...state, items: newItems };
        }

        case DO_MODIFY: {
          const { itemId } = action;

          if (state.items.has(itemId)) {
            const payload = state.items.get(itemId)!;
            const newItems = new Map(state.items);
            newItems.set(itemId, { ...payload, ...action.payload });
            return { ...state, items: newItems };
          }

          return state;
        }

        case DO_SET: {
          const { items, selections } = action.payload;
          return {
            ...initialState,
            items: new Map(Object.entries(items)),
            selections: new Map(Object.entries(selections))
          };
        }

        case DO_CLEAR: {
          return initialState;
        }

        case DO_SET_VISIBILITY_FILTER: {
          return { ...state, visibilityFilter: action.visibilityFilter };
        }

        case DO_EXPAND: {
          const { itemId, expanded } = action;

          if (!state.selections.has(itemId) && expanded) {
            const newSelections = new Map(state.selections);
            newSelections.set(itemId, []);
            return { ...state, selections: newSelections };
          } else if (state.selections.has(itemId) && !expanded) {
            const newSelections = new Map(state.selections);
            newSelections.delete(itemId);
            return { ...state, selections: newSelections };
          }

          return state;
        }
      }
    }

    return state;
  };

  return reducer;
};

import { all, call, put, take } from "redux-saga/effects";
import { SagaGenerator } from "../../generics";
import {
  CombinedState,
  Validator,
  PersistedItem,
  MutablePayloadMap,
  Errors,
  Manager,
  ManagerProgenyHandler,
  CreateAction,
  DestroyAction,
  ModifyAction,
  SetAction,
  SetVisibilityFilterAction,
  ExpandAction,
  StateSelectorMap
} from "./types";
import { makeSagaManager } from "./saga-manager";
import { makeManagerConstants } from "./manager-constants";
import { makeManagerActionCreators } from "./manager-action-creators";
import { makeManagerReducer } from "./manager-reducer";
import { makeManagerProgenyHandler } from "./manager-progeny-handler";

let counter = 0;

export const makeManager = <T>(
  managerId: string,
  parentManagerId?: string
): Manager<T> => {
  const CONSTS = makeManagerConstants(managerId);
  const {
    CREATE,
    DESTROY,
    MODIFY,
    SET,
    SET_VISIBILITY_FILTER,
    EXPAND
  } = CONSTS;

  const actionCreators = makeManagerActionCreators<T>(CONSTS);
  const {
    doCreate,
    doDestroy,
    doModify,
    doSet,
    doSetVisibilityFilter,
    doExpand
  } = actionCreators;

  const reducer = makeManagerReducer<T>(CONSTS);

  const makeTmpId = () => managerId + "_" + counter++;

  const stateSelectors: StateSelectorMap<T> = {
    getState: (state: CombinedState) => state[managerId],
    getItemMap: (state: CombinedState) => state[managerId].items,
    getSelectionMap: (state: CombinedState) => state[managerId].selections,
    getVisibilityFilter: (state: CombinedState) =>
      state[managerId].visibilityFilter
  };

  const sagaManager = makeSagaManager();

  const validators: Set<Validator<T>> = new Set();

  const addValidator = (validate: Validator<T>) => {
    if (parentManagerId) {
      throw new Error(
        `A child childManager cannot have validators: ${managerId}`
      );
    }

    validators.add(validate);
  };

  function* validateSaga(payload: PersistedItem<T>): SagaGenerator {
    const validationMessages: string[][] = yield all(
      Array.from(validators).map((validate) => call(validate, payload))
    );

    const errors: Set<string> = new Set();
    validationMessages.forEach((msgs) =>
      msgs.forEach((msg) => errors.add(msg))
    );

    return Array.from(errors);
  }

  if (!parentManagerId) {
    sagaManager.add(CREATE, function* (): SagaGenerator {
      const { payload }: CreateAction<T> = yield take(CREATE);
      const errors: Errors | undefined = yield* validateSaga(payload);
      yield put(doCreate(makeTmpId(), { ...payload, errors } as const));
    });

    sagaManager.add(DESTROY, function* (): SagaGenerator {
      const { itemId }: DestroyAction = yield take(DESTROY);
      yield put(doDestroy(itemId));
    });

    sagaManager.add(MODIFY, function* (): SagaGenerator {
      const { itemId, payload }: ModifyAction<T> = yield take(MODIFY);
      const errors: Errors = yield* validateSaga(payload);
      yield put(doModify(itemId, { ...payload, errors }));
    });

    sagaManager.add(SET, function* (): SagaGenerator {
      const {
        payload: { items: persistedItemMap, selections }
      }: SetAction<T> = yield take(SET);
      const payloadMap: MutablePayloadMap<T> = {};

      for (let [itemId, persistedItem] of Object.entries(persistedItemMap)) {
        const errors: Errors = yield* validateSaga(persistedItem);
        payloadMap[itemId] = { ...persistedItem, errors };
      }

      yield put(doSet({ items: payloadMap, selections }));
    });

    sagaManager.add(SET_VISIBILITY_FILTER, function* (): SagaGenerator {
      const { visibilityFilter }: SetVisibilityFilterAction = yield take(
        SET_VISIBILITY_FILTER
      );
      yield put(doSetVisibilityFilter(visibilityFilter));
    });

    sagaManager.add(EXPAND, function* (): SagaGenerator {
      const { itemId }: ExpandAction = yield take(EXPAND);
      yield put(doExpand(itemId, true));
    });
  }

  const manager = {
    managerId,
    CONSTS,
    actionCreators,
    stateSelectors,
    reducer,
    sagaManager,
    addValidator,
    get progenyHandler(): ManagerProgenyHandler<T> {
      throw new Error("Manager.progenyHandler accessed before being set");
    }
  };

  Object.defineProperty(manager, "progenyHandler", {
    value: makeManagerProgenyHandler<T>(manager, makeManager),
    writable: false,
    enumerable: true,
    configurable: false
  });

  return manager;
};

import createSagaMiddleware from "redux-saga";
import { take } from "redux-saga/effects";
import { SagaGenerator } from "../../generics";
import { SagaManager } from "./types";

export const sagaMiddleware = createSagaMiddleware();

export const makeSagaManager = (): SagaManager => {
  const triggers: Map<string, string> = new Map();
  const sagas: Map<string, () => SagaGenerator> = new Map();
  const runningSagas: Set<string> = new Set();

  const add = (
    sagaName: string,
    saga: () => SagaGenerator,
    trigger?: string
  ) => {
    if (sagas.has(sagaName)) {
      return;
    }

    if (trigger) {
      triggers.set(sagaName, trigger);
    }

    const controllableSaga = function* (): SagaGenerator {
      if (triggers.has(sagaName)) {
        yield take(triggers.get(sagaName));
      }

      do {
        yield* saga();
      } while (runningSagas.has(sagaName));
    };

    sagas.set(sagaName, controllableSaga);
  };

  const start = (sagaName: string) => {
    if (sagas.has(sagaName) && !runningSagas.has(sagaName)) {
      sagaMiddleware.run(sagas.get(sagaName)!);
      runningSagas.add(sagaName);
    }
  };

  const stop = (sagaName: string) => {
    runningSagas.delete(sagaName);
  };

  const remove = (sagaName: string) => {
    stop(sagaName);
    sagas.delete(sagaName);
    triggers.delete(sagaName);
  };

  const replace = (
    sagaName: string,
    saga: () => SagaGenerator,
    trigger?: string
  ) => {
    const running = runningSagas.has(sagaName);
    remove(sagaName);
    add(sagaName, saga, trigger);
    if (running) {
      start(sagaName);
    }
  };

  const startAll = () => {
    for (let sagaName of sagas.keys()) {
      start(sagaName);
    }
  };

  const stopAll = () => {
    runningSagas.clear();
  };

  const run = (
    sagaName?: string,
    saga?: () => SagaGenerator,
    trigger?: string
  ) => {
    if (!sagaName) {
      startAll();
    } else {
      if (saga) {
        add(sagaName, saga, trigger);
      }
      start(sagaName);
    }
  };

  return {
    add,
    remove,
    replace,
    start,
    stop,
    startAll,
    stopAll,
    run
  };
};

import { VisibilityFilter, TodoStates, View } from "./types";
