import createSagaMiddleware from "redux-saga";
import { take } from "redux-saga/effects";
import { SagaGenerator } from "../../../generics";
import { SagaManager as SagaManagerInterface } from "../types";

export const sagaMiddleware = createSagaMiddleware();

export class SagaManager implements SagaManagerInterface {
  private triggers: Map<string, string> = new Map();
  private sagas: Map<string, () => SagaGenerator> = new Map();
  private runningSagas: Set<string> = new Set();

  add(sagaName: string, saga: () => SagaGenerator, trigger?: string) {
    if (this.sagas.has(sagaName)) {
      return;
    }

    if (trigger) {
      this.triggers.set(sagaName, trigger);
    }

    const triggers = this.triggers;
    const runningSagas = this.runningSagas;

    const controllableSaga = function* (): SagaGenerator {
      if (triggers.has(sagaName)) {
        yield take(triggers.get(sagaName));
      }

      do {
        yield* saga();
      } while (runningSagas.has(sagaName));
    };

    this.sagas.set(sagaName, controllableSaga);
  }

  remove(sagaName: string) {
    this.stop(sagaName);
    this.sagas.delete(sagaName);
    this.triggers.delete(sagaName);
  }

  replace(sagaName: string, saga: () => SagaGenerator, trigger?: string) {
    const running = this.runningSagas.has(sagaName);
    this.remove(sagaName);
    this.add(sagaName, saga, trigger);
    if (running) {
      this.start(sagaName);
    }
  }

  start(sagaName: string) {
    if (this.sagas.has(sagaName) && !this.runningSagas.has(sagaName)) {
      sagaMiddleware.run(this.sagas.get(sagaName)!);
      this.runningSagas.add(sagaName);
    }
  }

  stop(sagaName: string) {
    this.runningSagas.delete(sagaName);
  }

  startAll() {
    for (let sagaName of this.sagas.keys()) {
      this.start(sagaName);
    }
  }

  stopAll() {
    this.runningSagas.clear();
  }

  run(sagaName?: string, saga?: () => SagaGenerator, trigger?: string) {
    if (!sagaName) {
      this.startAll();
    } else {
      if (saga) {
        this.add(sagaName, saga, trigger);
      }
      this.start(sagaName);
    }
  }
}

import { RESTDataSource } from "apollo-datasource-rest";
import { GQLLaunch } from "../__types__/spacex-schema";

export class LaunchAPI extends RESTDataSource {
  baseURL = "https://api.spacexdata.com/v2/";

  async getAllLaunches() {
    const response = await this.get("launches");
    return Array.isArray(response)
      ? response.map((launch) => this.launchReducer(launch))
      : [];
  }

  launchReducer(launch: GQLLaunch) {
    return {
      id: launch.flight_number || 0,
      cursor: `${launch.launch_date_unix}`,
      site: launch.launch_site?.site_name,
      mission: {
        name: launch.mission_name,
        missionPatchSmall: launch.links?.mission_patch_small,
        missionPatchLarge: launch.links?.mission_patch
      },
      rocket: {
        id: launch.rocket?.rocket_id,
        name: launch.rocket?.rocket_name,
        type: launch.rocket?.rocket_type
      }
    };
  }

  async getLaunchById({ launchId }: { launchId: string }) {
    const response = await this.get("launches", {
      flight_number: parseInt(launchId, 10)
    } as Partial<GQLLaunch>);
    return this.launchReducer(response[0]);
  }

  getLaunchesByIds({ launchIds }: { launchIds: readonly string[] }) {
    return Promise.all(
      launchIds.map((launchId) => this.getLaunchById({ launchId }))
    );
  }
}

import { DataSource, DataSourceConfig } from "apollo-datasource";
import isEmail from "isemail";
import { GQLUser } from "../__types__/schema";
import { Store, Trip, User, TripDataType } from "../server/utils";

export class UserAPI<Context extends { user?: GQLUser }> extends DataSource<
  Context
> {
  private context: Context | undefined;
  private store: Store;

  constructor({ store }: { store: Store }) {
    super();
    this.store = store;
  }

  /**
   * This is a function that gets called by ApolloServer when being setup.
   * This function gets called with the datasource config including things
   * like caches and context. We'll assign this.context to the request context
   * here, so we can know about the user making requests
   */
  initialize(config: DataSourceConfig<Context>) {
    this.context = config.context;
  }

  /**
   * User can be called with an argument that includes email, but it doesn't
   * have to be. If the user is already on the context, it will use that user
   * instead
   */
  async findOrCreateUser({ email: emailArg }: Partial<GQLUser> = {}) {
    const email = this.context?.user?.email || emailArg;
    if (!email || !isEmail.validate(email)) return null;

    const users = await this.store.users.findOrCreate<User>({
      where: { email }
    });
    return users && users[0] ? users[0] : null;
  }

  async bookTrips({ launchIds }: { launchIds: readonly string[] }) {
    const userId = this.context?.user?.id;
    if (!userId) return;

    let results = [];

    // for each launch id, try to book the trip and add it to the results array
    // if successful
    for (const launchId of launchIds) {
      const res = await this.bookTrip({ launchId });
      if (res) results.push(res);
    }

    return results;
  }

  async bookTrip({ launchId }: { launchId: string }) {
    const userId = this.context?.user?.id;
    if (!userId) return;

    const res = await this.store.trips.findOrCreate<Trip>({
      where: { userId, launchId }
    });
    return res && res.length ? (res[0].get() as { id: number }) : false;
  }

  async cancelTrip({ launchId }: { launchId: string }) {
    const userId = this.context?.user?.id;
    if (!userId) return;

    return !!this.store.trips.destroy({ where: { userId, launchId } });
  }

  async getLaunchIdsByUser() {
    const userId = this.context?.user?.id;
    if (!userId) return;

    const found = await this.store.trips.findAll<Trip>({
      where: { userId }
    });

    return found && found.length
      ? found.map((l) => l.dataValues.launchId).filter((l) => !!l)
      : [];
  }

  async isBookedOnLaunch({ launchId }: { launchId: string }) {
    if (!this.context || !this.context.user) return false;

    const userId = this.context.user.id;
    const found = await this.store.trips.findAll<Trip>({
      where: { userId, launchId }
    });

    return found && found.length > 0;
  }
}

